tool.command:
LANG=en_US.UTF-8 monocle3 plotCells --reduction-method '$(reduction_method)' --norm-method '$(norm_method)' #if $xdim --xdim '$(xdim)' #end if #if $ydim --ydim '$(ydim)' #end if #if $color_cells_by --color-cells-by '$(color_cells_by)' #end if #if $genes --genes '$(genes)' #end if #if $cell_size --cell-size '$(cell_size)' #end if #if $alpha --alpha '$(alpha)' #end if #if $label_cell_groups --label-cell-groups #end if #if $no_trajectory_graph --no-trajectory-graph #end if #if $label_groups_by_cluster --label-groups-by-cluster #end if #if $label_leaves --label-leaves #end if #if $label_roots --label-roots #end if #if $label_branch_points --label-branch-points #end if
@INPUT_OPTS@ output.png
@VERBOSE@

tool.help:
===================================== Monocle3 cell plotting (`plot_cells`) =====================================
Display various information about cells (clustering, pseudotime, the inferred graph, user-provided annotations, gene expression) in reduced dimensions.
@HELP@
@VERSION_HISTORY@
