tool.command:
LANG=en_US.UTF-8 monocle3 preprocess --method '${method}' #if $num_dim --num-dim '${num_dim}' #end if --norm-method '${norm_method}' #if $use_genes --use-genes '${use_genes}' #end if #if $residual_model_formula_str --residual-model-formula-str '${residual_model_formula_str}' #end if #if $pseudo_count --pseudo-count '${pseudo_count}' #end if #if $no_scaling --no-scaling #end if
@INPUT_OPTS@ @OUTPUT_OPTS@
@VERBOSE@

tool.help:
==================================================================================== Preprocess Monocle3 object up to initial dimensionality reduction (`preprocess_cds`) ====================================================================================
Preprocess a Monocle3 object with size factor scaling of the count data, optionally log transforming, and computing an initial dimensionality reduction. This space is used as a starting point for a number of subsequent analyses.
@HELP@
@VERSION_HISTORY@
