tool.command:
ln -s '${input_obj_file}' input.h5 &&
PYTHONIOENCODING=utf-8 scanpy-find-variable-genes
    --flavor '${flavor}'
#if $min_mean
    --mean-limits ${min_mean} ${max_mean}
#end if    
#if $min_disp
    --disp-limits ${min_disp} ${max_disp}
#end if    
#if $n_top_gene
    --n-top-genes ${n_top_gene}
#end if
#if $span
    --span ${span}
#end if
    --n-bins '${n_bin}'
    ${filter}
    @INPUT_OPTS@
    @OUTPUT_OPTS@

tool.help:
============================================================== Mark highly variable genes (`scanpy.pp.highly_variable_genes`) ==============================================================
Depending on `flavor`, this reproduces the R-implementations of Seurat or Cell Ranger.
The normalized dispersion is obtained by scaling with the mean and standard deviation of the dispersions for genes falling into a given bin for mean expression of genes. This means that for each bin of mean expression, highly variable genes are selected.
@HELP@
@VERSION_HISTORY@
