tool.command:
ln -s '${input_obj_file}' input.h5 &&
PYTHONIOENCODING=utf-8 scanpy-cli plot paga
#if $use_key
    --use-key '${use_key}'
#end if
    --layout ${layout}
#if $basis
    --basis ${basis}
#end if
#if $settings.default == "false"
    --threshold ${settings.threshold}
    #if $settings.root
        --root ${settings.root}
    #end if
        ${settings.single_component}
    #if $settings.solid_edges
        --solid-edges ${settings.solid_edges}
    #end if
    #if $settings.color
        --color '${settings.color}'
    #end if
    #if $settings.node_size
        --node-size-scale ${settings.node_size}
    #end if
    #if $settings.node_font
        --fontsize ${settings.node_font}
    #end if
    #if $settings.edge_width
        --edge-width-scale ${settings.edge_width}
    #end if
#end if
    @INPUT_OPTS@
    @PLOT_OPTS@

tool.help:
=============================================================== Plot PAGA-inferred trajectories (`scanpy.pl.paga/paga_compare`) ===============================================================
Depending on the selected options, it yields a plot of a graph representing inferred trajectory, or a trajectory graph side-by-side with a scatter plot of cells embedded to space of reduced dimensionality in png format.
It requires running PAGA, first.
@HELP@
@VERSION_HISTORY@
