tool.command:
echo "coords = [ {'file':'$tsneCoordinates', 'shortLabel':'t-SNE on WGCNA'} ]" > ./config-file;
echo "meta = '$cellMetadata'" >> ./config-file;
echo "name = 'sample'" >> ./config-file;
